import React from 'react';

/**
 * タスクのコンポ
 */
class Task extends React.Component {

	constructor() {
		super();
		this.state = {
		}
	}

	render() {
		const todoList = this.props.todoList.map(todo =>
			<Do
				key={todo.id}
				{...todo}
			/>
		)
		return (
			<div>
				<li>
					<input type='checkbox' />
					{/*listを作って追加していく*/}
					{this.state.todoList}
				</li>
				<hr />
			</div>
		);
	}
}

export default Task;
