import React from 'react';

import Button from './Button';
import PropTypes from 'prop-types';
import { styles } from 'ansi-colors';

/**
 * ToDo入力フィールドのコンポ
 */
class InputFeild extends React.Component {

    constructor() {
        super();
        this.state = {
            task: '',
            todoList: []
        }

        //おまじない
        this.handleTask = this.handleTask.bind(this);
        this.putMsg = this.putMsg.bind(this);
    }

    // stateに保持しておく値を書き換える
    handleTask(event) {
        const inputValue = event.target.value
        this.setState({
            task: inputValue
        });
    }

    //関数
    putMsg() {
        const text = this.state.task
        const aftTodoList = this.state.todoList
        aftTodoList.push(text);
        //setStateは関数
        this.setState({
            todoList: aftTodoList,//入力値
            task: ''
        });
    }

    render() {
        return (
            <div>
                <input className={styles.textarea} type="text" name="text" value={this.state.task} onChange={this.handleTask} />
                <Button className={styles.Button} label={this.props.label} onClick={this.putMsg} />
                {/* <button value={this.props.label} /> */}
            </div>
        );
    }
}

InputFeild.propTypes = {
    label: PropTypes.string.isRequired
}

export default InputFeild;
