import React from 'react';

import Button from './Button';
import InputField from './InputField';
import List from './List';

class Todo extends React.Component {

    constructor() {
        super();
        this.state = {
            task: '',
            todoList: [
                { task: '牛乳を買う', flag: false },
                { task: '豆乳を買う', flag: false }
            ],
            selTask: ''
        }

        //おまじない
        // this.setList = this.setList.bind(this);
        this.compTask = this.compTask.bind(this);
        this.delTask = this.delTask.bind(this);
        this.putMsg = this.putMsg.bind(this);
    }

    // 戻り値をここで設定したい
    // setList(value) {
    //     this.setList({
    //         todoList: []
    //     })
    // }

    putMsg() {
        const text = this.state.task
        const aftTodoList = this.state.todoList
        aftTodoList.push({ task: text, flag: false });
        //setStateは関数
        this.setState({
            todoList: aftTodoList,//入力値
            task: ''
        });
    }

    compTask() {
        this.setState({
        });
    }

    delTask() {
        this.setState({
        });
    }

    render() {
        return (
            <div>
                <InputField label="追加" todoList={this.state.todoList} />
                <Button label="追加" onClick={this.putMsg} />
                <hr />
                <Button label="完了" onClick={this.compTask} />
                <Button label="削除" onClick={this.delTask} />
                <hr />
                <List todoList={this.state.todoList} />
            </div>
        );
    }
}

export default Todo;
